import React, { useState } from 'react';
import './CollapseAbout.scss';

const CollapseAbout = (props) => {

    // Récupérer les informations
    const title = props.title;

    // Variable(s) d'état
    const [isOpen, setIsOpen] = useState(false);

    function openCollapse()
    {
        setIsOpen(true);
    }

    function closeCollapse()
    {
        setIsOpen(false);
    }

    return (
        <div className='collapseAbout'>
            <div className='close_collapseAbout'>
                <div className='ta_collapseAbout'>
                    <h2>{title}</h2>
                    {isOpen ? <img src="../images/arrowUp.png" alt="" onClick={closeCollapse} /> : <img src="../images/arrowDown.png" alt="" onClick={openCollapse}/>}
                </div>
                {isOpen ? (
                <div className='open_collapseAbout'>
                    {
                        title === "Fiabilité" ? (<p>Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées  par nos équipes.</p>) : 
                        title === "Respect" ? (<p>La bienveillance fait partie des valeurs fondatrices de Kasa. Tout comportement discriminatoire ou de perturbation du voisinage entraînera une exclusion de notre plateforme.</p>) : 
                        title === "Service" ? (<p>Ok Serv</p>) : 
                        title === "Sécurité" ? (<p>Ok Sécu</p>) : 
                        ""
                    }
                </div>
            ) : ""}
            </div>
        </div>
    );
};

export default CollapseAbout;
