import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import data from '../../data/data.json';
import './Logement.scss';

const Logement = () => {
    
    // Récupérer l'id de l'url
    // Enregistrer le logement de l'id
    const {id} = useParams();
    const logement = data.find(logement => logement.id === id)
    const [index, setIndex] = useState(0);
    console.log(logement)
    console.log(logement.pictures.length)

    // Next Image Function
    function nextImage()
    {
        if (index + 1 < logement.pictures.length)
        {
            setIndex(index + 1);
        }
        else 
        {
            setIndex(0)
        }
    }

    // Previous Image Function
    function previousImage()
    {
        console.log("index", index)
        if (index === 0)
        {
            setIndex(logement.pictures.length - 1);
        }
    }

    return (
        <main className='Logement'>
            <div className='images_container'>
                <img className='picture' src={logement.pictures[index]} alt="" />
                <img className='arrowRight' src="../images/arrowRight.png" alt="" onClick={nextImage}/>
                <img className='arrowLeft' src="../images/arrowLeft.png" alt="" onClick={previousImage} />
                <p>{index + 1}/{logement.pictures.length}</p>
            </div>
        </main>
    );
};

export default Logement;
