import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import data from '../../data/data.json';
import './Logement.scss';
import Picture from '../../composants/Picture/Picture';
import Tag from '../../composants/Tag/Tag';
import Rating from '../../composants/Rating/Rating';
import Host from '../../composants/Host/Host';
import CollapseLogement from '../../composants/CollapseLogement/CollapseLogement';
import Error from '../../pages/Error/Error';

const Logement = () => {
    
    // Récupérer le logement grâce à l'id de l'url
    const {id} = useParams();
    const [logement, setLogement] = useState({
        tags: [],
        equipments: [],
        pictures :[],
        rating: "",
        host: {
            name : "",
            picture : ""
        }
    })

    useEffect(() => {
        data.map((logement) => {
            if (logement.id === id) {
                console.log('ici')
                setLogement(logement);
            }
            return null;
        })
    }, [id]);

    if(logement.title === undefined){
        return <Error/>
    }

   // const logement = data.find(logement => logement.id === id)

    return (
        <main className='Logement'>
            <Picture logement={logement}></Picture>
            <div className='informations_logement'>
                <div className='tlthr_container'>
                    <div className='tlt_container'>
                        <h1>{logement.title}</h1>
                        <h2>{logement.location}</h2>
                        <Tag logement={logement}></Tag>
                    </div>
                    <div className='hr_container'>
                        <Host logement={logement}></Host>
                        <Rating logement={logement}></Rating>
                    </div>
                </div>
                <div className='collapse_container'>
                    <CollapseLogement title="Description" logement={logement} />
                    <CollapseLogement title="Équipements" logement={logement} />
                </div>
            </div>
        </main>
    );
};

export default Logement;
