import React, { useState } from 'react';
import './Collapse.scss';

const Collapse = (props) => {

    // Récupérer les informations
    const title = props.title;
    const logement = props.logement;

    // Variable(s) d'état
    const [isOpen, setIsOpen] = useState(false);

    function openCollapse()
    {
        setIsOpen(true);
    }

    function closeCollapse()
    {
        setIsOpen(false);
    }
    
    return (
        <div className='Collapse'>
            <div className='ta_container'>
                <h2>{title}</h2>
                {isOpen ? <img src="../images/arrowUp.png" alt="" onClick={closeCollapse} /> : <img src="../images/arrowDown.png" alt="" onClick={openCollapse}/>}
            </div>
            {isOpen ? (
                <div className='open_collapse'>
                    {title !== "Équipements" ? (
                        <p>{logement.description}</p>
                    ) : (
                        <ul>
                            {logement.equipments.map((item) => (<li>{item}</li>))}
                        </ul>
                    )}
                </div>
            ) : ""}
        </div>
    );
};

export default Collapse;
